1036: recordNumber++;
1037: assertEquals(String.valueOf(recordNumber), record.get(0));
1038: }
1039: 1040: // Consecutive enhanced for loops with hasNext() peeking shouldn't break sequence
1041: parser = CSVFormat.DEFAULT.parse(new StringReader(fiveRows));
1042: recordNumber = 0;
1043: for (CSVRecord record : parser) {
1044: recordNumber++;
1045: assertEquals(String.valueOf(recordNumber), record.get(0));
1046: if (recordNumber >= 2) {
1047: break;
1048: }
1049: }
1050: parser.iterator().hasNext();
