54: public void testOffsetWithObjectFieldsUsingReader() throws Exception
55: {
56: final JsonFactory f = new JsonFactory();
57: char[] c = "{\"f1\":\"v1\",\"f2\":{\"f3\":\"v3\"},\"f4\":[true,false],\"f5\":5}".toCharArray();
58: //            1      6      11    16 17    22      28    33 34 39      46    51
59: JsonParser p = f.createParser(c);
60: 61: assertEquals(JsonToken.START_OBJECT, p.nextToken());
62: 63: assertEquals(JsonToken.FIELD_NAME, p.nextToken());
64: assertEquals(1L, p.getTokenLocation().getCharOffset());
65: assertEquals(JsonToken.VALUE_STRING, p.nextToken());
66: assertEquals(6L, p.getTokenLocation().getCharOffset());
67: 68: assertEquals("f2", p.nextFieldName());
69: assertEquals(11L, p.getTokenLocation().getCharOffset());
70: assertEquals(JsonToken.START_OBJECT, p.nextValue());
71: assertEquals(16L, p.getTokenLocation().getCharOffset());
72: 73: assertEquals("f3", p.nextFieldName());
74: assertEquals(17L, p.getTokenLocation().getCharOffset());
75: assertEquals(JsonToken.VALUE_STRING, p.nextValue());
76: assertEquals(22L, p.getTokenLocation().getCharOffset());
77: assertEquals(JsonToken.END_OBJECT, p.nextToken());
78: 79: assertEquals("f4", p.nextFieldName());
80: assertEquals(28L, p.getTokenLocation().getCharOffset());
81: assertEquals(JsonToken.START_ARRAY, p.nextValue());
82: assertEquals(33L, p.getTokenLocation().getCharOffset());
83: 84: assertEquals(JsonToken.VALUE_TRUE, p.nextValue());
85: assertEquals(34L, p.getTokenLocation().getCharOffset());
86: 87: assertEquals(JsonToken.VALUE_FALSE, p.nextValue());
88: assertEquals(39L, p.getTokenLocation().getCharOffset());
89: assertEquals(JsonToken.END_ARRAY, p.nextToken());
90: 91: assertEquals("f5", p.nextFieldName());
92: assertEquals(46L, p.getTokenLocation().getCharOffset());
93: assertEquals(JsonToken.VALUE_NUMBER_INT, p.nextToken());
94: assertEquals(51L, p.getTokenLocation().getCharOffset());
95: assertEquals(JsonToken.END_OBJECT, p.nextToken());
96: 97: p.close();
98: }

