public void testAllowNaN() throws Exception {
_testAllowNaN(false);
_testAllowNaN(true);
}
 private void _testAllowNaN(boolean useStream) throws Exception{final String JSON = "[ NaN]";JsonFactory f = new JsonFactory();assertFalse(f.isEnabled(JsonParser.Feature.ALLOW_NON_NUMERIC_NUMBERS));JsonParser jp = useStream ? createParserUsingStream(f, JSON, "UTF-8"): createParserUsingReader(f, JSON);assertToken(JsonToken.START_ARRAY, jp.nextToken());try {jp.nextToken();fail("Expected exception");} catch (Exception e) {verifyException(e, "non-standard");} finally {jp.close();}f.configure(JsonParser.Feature.ALLOW_NON_NUMERIC_NUMBERS, true);jp = useStream ? createParserUsingStream(f, JSON, "UTF-8"): createParserUsingReader(f, JSON);assertToken(JsonToken.START_ARRAY, jp.nextToken());assertToken(JsonToken.VALUE_NUMBER_FLOAT, jp.nextToken());double d = jp.getDoubleValue();assertTrue(Double.isNaN(d));assertEquals("NaN", jp.getText());try {/*BigDecimal dec =*/ jp.getDecimalValue();fail("Should fail when trying to access NaN as BigDecimal");} catch (NumberFormatException e) {e.printStackTrace();verifyException(e, "can not be represented as BigDecimal");}

