63: // and then peel them off
p = f.createParser(b, 3, b.length-5);
assertToken(JsonToken.START_OBJECT, p.nextToken());
67: loc = p.getTokenLocation();
assertEquals(0L, loc.getByteOffset());
assertEquals(-1L, loc.getCharOffset());

