public void testDynamicRootName() throws IOException
{
String xml;
71: ObjectWriter w = _xmlMapper.writer().withRootName("rudy");
73: xml = w.writeValueAsString(new StringBean("foo"));
assertEquals("<rudy><text>foo</text></rudy>", xml);
76: xml = w.writeValueAsString(new StringBean(null));
assertEquals("<rudy><text/></rudy>", xml);
79: // and even with null will respect configured root name
xml = w.writeValueAsString(null);
assertEquals("<rudy/>", xml);
}
