public void addVirtualWrapping(Set<String> namesToWrap)
String name = _xmlTokens.getLocalName();
if (name != null && namesToWrap.contains(name)) {
_xmlTokens.repeatStartElement();
_namesToWrap = namesToWrap;
_parsingContext.setNamesToWrap(namesToWrap);
}

public String getCurrentName() throws IOException
if (_currToken == JsonToken.START_OBJECT || _currToken == JsonToken.START_ARRAY) {
name = _parsingContext.getCurrentName();
if (name == null) {
return name;
}

public void close() throws IOException
if (!_closed) {
_closed = true;
if (_ioContext.isResourceManaged() || isEnabled(JsonParser.Feature.AUTO_CLOSE_SOURCE)) {
_xmlTokens.closeCompletely();
_releaseBuffers();
}
}

public XmlReadContext getParsingContext() {
return _parsingContext;
}

public boolean isExpectedStartArrayToken()
JsonToken t = _currToken;
if (t == JsonToken.START_OBJECT) {
_currToken = JsonToken.START_ARRAY;
_parsingContext.convertToArray();
_nextToken = null;
_xmlTokens.skipAttributes();
return true;
}

public String getText() throws IOException
if (_currToken == null) {
switch (_currToken) {
return _currText;
}

public XmlReadContext(XmlReadContext parent, int type, int lineNr, int colNr)
super();
_type = type;
_parent = parent;
_lineNr = lineNr;
_columnNr = colNr;
_index = -1;
}

public static XmlReadContext createRootContext(int lineNr, int colNr) {
return new XmlReadContext(null, TYPE_ROOT, lineNr, colNr);
}

public final XmlReadContext createChildObjectContext(int lineNr, int colNr)
XmlReadContext ctxt = _child;
if (ctxt == null) {
_child = ctxt = new XmlReadContext(this, TYPE_OBJECT, lineNr, colNr);
return ctxt;
}

public void setCurrentName(String name) {
_currentName = name;
}

public void setNamesToWrap(Set<String> namesToWrap) {
_namesToWrap = namesToWrap;
}

public Set<String> getNamesToWrap() {
return _namesToWrap;
}

protected void convertToArray() {
_type = TYPE_ARRAY;
}

public XmlTokenStream(XMLStreamReader xmlReader, Object sourceRef)
{
_sourceReference = sourceRef;
if (xmlReader.getEventType() != XMLStreamConstants.START_ELEMENT) {
_xmlReader = Stax2ReaderAdapter.wrapIfNecessary(xmlReader);
_currentState = XML_START_ELEMENT;
_localName = _xmlReader.getLocalName();
_namespaceURI = _xmlReader.getNamespaceURI();
_attributeCount = _xmlReader.getAttributeCount();
}

public int next() throws IOException
if (_repeatElement != 0) {
return (_currentState = _handleRepeatElement());
return _next();
}
