public String absUrl(String attributeKey) {
Validate.notEmpty(attributeKey);
String relUrl = attr(attributeKey);
if (!hasAttr(attributeKey)) {
base = new URL(baseUri);
}
URL abs = new URL(base, relUrl);
return abs.toExternalForm();
}

public void traverse(Node root) {
Node node = root;
int depth = 0;
while (node != null) {
visitor.head(node, depth);
if (node.childNodes().size() > 0) {
node = node.childNode(0);
depth++;
while (node.nextSibling() == null && depth > 0) {
visitor.tail(node, depth);
node = node.parent();
depth--;
visitor.tail(node, depth);
if (node == root)
break;
node = node.nextSibling();
}

protected Node(String baseUri, Attributes attributes) {
Validate.notNull(baseUri);
Validate.notNull(attributes);
childNodes = new ArrayList<Node>(4);
this.baseUri = baseUri.trim();
this.attributes = attributes;
}

protected void setParentNode(Node parentNode) {
if (this.parentNode != null)
this.parentNode = parentNode;
}

public String attr(String attributeKey) {
Validate.notNull(attributeKey);
if (hasAttr(attributeKey))
return attributes.get(attributeKey);
}

protected Node() {
childNodes = Collections.EMPTY_LIST;
attributes = null;
}

public Node childNode(int index) {
return childNodes.get(index);
}

public Node nextSibling() {
if (parentNode == null)
return null; // root
List<Node> siblings = parentNode.childNodes;
Integer index = siblingIndex();
Validate.notNull(index);
if (siblings.size() > index+1)
return siblings.get(index+1);
return null;
}

protected void addChildren(Node... children) {
for (Node child: children) {
reparentChild(child);
childNodes.add(child);
child.setSiblingIndex(childNodes.size()-1);
}

public int hashCode() {
int result = parentNode != null ? parentNode.hashCode() : 0;
result = 31 * result + (attributes != null ? attributes.hashCode() : 0);
return result;
}

public TextNode(String text, String baseUri) {
this.baseUri = baseUri;
this.text = text;
}

private void reparentChild(Node child) {
if (child.parentNode != null)
child.setParentNode(this);
}

public boolean hasAttr(String attributeKey) {
Validate.notNull(attributeKey);
return attributes.hasKey(attributeKey);
}

private void parseTextNode() {
if (tq.peek().equals('<')) {
String text = tq.consumeTo("<");
textNode = TextNode.createFromEncoded(text, baseUri);
last().appendChild(textNode);
}

public Node parent() {
return parentNode;
}

public NodeTraversor(NodeVisitor visitor) {
this.visitor = visitor;
}

public String nodeName() {
return "#document";
}

public List<Node> childNodes() {
return Collections.unmodifiableList(childNodes);
}

public String nodeName() {
return tag.getName();
}

public Integer siblingIndex() {
return siblingIndex;
