Evaluator parse() {
tq.consumeWhitespace();
if (tq.matchesAny(combinators)) { // if starts with a combinator, use root as elements
findElements();
while (!tq.isEmpty()) {
boolean seenWhite = tq.consumeWhitespace();
if (tq.matchChomp(",")) {
CombiningEvaluator.Or or = new CombiningEvaluator.Or(evals);
evals.clear();
evals.add(or);
while (!tq.isEmpty()) {
String subQuery = tq.chompTo(",");
or.add(parse(subQuery));
}
} else if (tq.matchesAny(combinators)) {
combinator(tq.consume());
} else if (seenWhite) {
findElements(); // take next el, #. etc off queue
}
if (evals.size() == 1)
return evals.get(0);
}

private void byAttribute() {
TokenQueue cq = new TokenQueue(tq.chompBalanced('[', ']')); // content queue
String key = cq.consumeToAny("=", "!=", "^=", "$=", "*=", "~="); // eq, not, start, end, contain, match, (no val)
Validate.notEmpty(key);
cq.consumeWhitespace();
if (cq.isEmpty()) {
if (cq.matchChomp("="))
evals.add(new Evaluator.AttributeWithValue(key, cq.remainder()));
}

public String chompBalanced(char open, char close) {
StringBuilder accum = new StringBuilder();
int depth = 0;
char last = 0;
if (isEmpty()) break;
Character c = consume();
if (last == 0 || last != ESC) {
if (c.equals(open))
depth++;
else if (c.equals(close))
depth--;
if (depth > 0 && last != 0)
accum.append(c); // don't include the outer match pair in the return
last = c;
} while (depth > 0);
return accum.toString();
}

