String consumeToAny(final char... chars) {
int start = pos;
OUTER: while (pos < length) {
for (int i = 0; i < chars.length; i++) {
if (input[pos] == chars[i])
break OUTER;
pos++;
return pos > start ? new String(input, start, pos-start) : "";
}

