static Document parseByteData(ByteBuffer byteData, String charsetName, String baseUri, Parser parser) {
Document doc = null;
if (charsetName == null) { // determine from meta. safe parse as UTF-8
docData = Charset.forName(defaultCharset).decode(byteData).toString();
doc = parser.parseInput(docData, baseUri);
Element meta = doc.select("meta[http-equiv=content-type], meta[charset]").first();
if (meta != null) { // if not found, will keep utf-8 as best attempt
String foundCharset = null;
if (meta.hasAttr("http-equiv")) {
foundCharset = getCharsetFromContentType(meta.attr("content"));
if (foundCharset == null && meta.hasAttr("charset")) {
if (foundCharset != null && foundCharset.length() != 0 && !foundCharset.equals(defaultCharset)) { // need to re-decode
foundCharset = foundCharset.trim().replaceAll("[\"']", "");
charsetName = foundCharset;
byteData.rewind();
docData = Charset.forName(foundCharset).decode(byteData).toString();
doc = null;
} else { // specified by content type header (or by user on file load)
if (docData.length() > 0 && docData.charAt(0) == UNICODE_BOM) {
if (doc == null) {
doc = parser.parseInput(docData, baseUri);
doc.outputSettings().charset(charsetName);
return doc;
}

static ByteBuffer readToByteBuffer(InputStream inStream, int maxSize) throws IOException {
Validate.isTrue(maxSize >= 0, "maxSize must be 0 (unlimited) or larger");
final boolean capped = maxSize > 0;
byte[] buffer = new byte[bufferSize];
ByteArrayOutputStream outStream = new ByteArrayOutputStream(bufferSize);
int remaining = maxSize;
read = inStream.read(buffer);
if (read == -1) break;
if (capped) {
outStream.write(buffer, 0, read);
return ByteBuffer.wrap(outStream.toByteArray());
}

static String getCharsetFromContentType(String contentType) {
if (contentType == null) return null;
Matcher m = charsetPattern.matcher(contentType);
if (m.find()) {
String charset = m.group(1).trim();
charset = charset.replace("charset=", "");
if (charset.length() == 0) return null;
if (Charset.isSupported(charset)) return charset;
}

private void combinator(char combinator) {
tq.consumeWhitespace();
String subQuery = consumeSubQuery(); // support multi > childs
Evaluator newEval = parse(subQuery); // the evaluator to add into target evaluator
boolean replaceRightMost = false;
if (evals.size() == 1) {
rootEval = currentEval = new CombiningEvaluator.And(evals);
evals.clear();
if (combinator == '>')
else if (combinator == ' ')
else if (combinator == '+')
else if (combinator == '~')
else if (combinator == ',') { // group or.
if (currentEval instanceof CombiningEvaluator.Or) {
or = new CombiningEvaluator.Or();
or.add(currentEval);
or.add(newEval);
currentEval = or;
}
if (replaceRightMost)
else rootEval = currentEval;
evals.add(rootEval);
}

