public boolean matchesAny(String... seq) {
for (String s : seq) {
if (matches(s))
return true;
return false;
}

private Tag setContainDataOnly() {
canContainBlock = false;
canContainInline = false;
preserveWhitespace = true;
return this;
}

public String consumeTagName() {
int start = pos;
while (!isEmpty() && (matchesWord() || matchesAny(':', '_', '-')))
pos++;
return queue.substring(start, pos);
}

private Tag setContainInlineOnly() {
canContainBlock = false;
canContainInline = true;
return this;
}

Tag getImplicitParent() {
return (!ancestors.isEmpty()) ? ancestors.get(0) : null;
}

public boolean matchesWord() {
return !isEmpty() && Character.isLetterOrDigit(queue.charAt(pos));
}

public boolean matchesWhitespace() {
return !isEmpty() && Character.isWhitespace(queue.charAt(pos));
}

public boolean isEmpty() {
return remainingLength() == 0;
}

private int remainingLength() {
return queue.length() - pos;
}

boolean requiresSpecificParent() {
return directDescendant;
}

public boolean matchesStartTag() {
return (remainingLength() >= 2 && queue.charAt(pos) == '<' && Character.isLetterOrDigit(queue.charAt(pos+1)));
}

public Attribute(String key, String value) {
Validate.notEmpty(key);
Validate.notNull(value);
this.key = key.trim().toLowerCase();
this.value = value;
}

protected Node(String baseUri, Attributes attributes) {
Validate.notNull(baseUri);
Validate.notNull(attributes);
childNodes = new ArrayList<Node>(4);
this.baseUri = baseUri.trim();
this.attributes = attributes;
}

public Element(Tag tag, String baseUri, Attributes attributes) {
super(baseUri, attributes);
Validate.notNull(tag);
this.tag = tag;
}

protected void addChildren(Node... children) {
for (Node child: children) {
reparentChild(child);
childNodes.add(child);
child.setSiblingIndex(childNodes.size()-1);
}

protected void setParentNode(Node parentNode) {
if (this.parentNode != null)
this.parentNode = parentNode;
}

private void reparentChild(Node child) {
if (child.parentNode != null)
child.setParentNode(this);
}

private OutputSettings outputSettings = new OutputSettings();
super(Tag.valueOf("#root"), baseUri);
}

public Element appendChild(Node child) {
Validate.notNull(child);
addChildren(child);
return this;
}
