691: Parser parser = Parser.htmlParser().setTrackErrors(500);
692: Document doc = Jsoup.parse(html, "http://example.com", parser);
693: 694: List<ParseError> errors = parser.getErrors();
695: assertEquals(5, errors.size());
696: assertEquals("20: Attributes incorrectly present on end tag", errors.get(0).toString());
697: assertEquals("35: Unexpected token [Doctype] when in state [InBody]", errors.get(1).toString());
698: assertEquals("36: Invalid character reference: invalid named referenece 'arrgh'", errors.get(2).toString());
699: assertEquals("50: Tag cannot be self closing; not a void tag", errors.get(3).toString());
700: assertEquals("61: Unexpectedly reached end of file (EOF) in input state [TagName]", errors.get(4).toString());

