ParseError(int pos, String errorMsg) {
this.pos = pos;
this.errorMsg = errorMsg;
}

void error(String errorMsg) {
if (errors.canAddError())
errors.add(new ParseError(reader.pos(), errorMsg));
}

public Parser setTrackErrors(int maxErrors) {
this.maxErrors = maxErrors;
return this;
}

Element insert(Token.StartTag startTag) {
if (startTag.isSelfClosing()) {
Element el = insertEmpty(startTag);
stack.add(el);
tokeniser.transition(TokeniserState.Data); // handles <script />, otherwise needs breakout steps from script data
tokeniser.emit(emptyEnd.reset().name(el.tagName()));  // ensure we get out of whatever state we are in. emitted for yielded processing
return el;
Element el = new Element(Tag.valueOf(startTag.name(), settings), baseUri, settings.normalizeAttributes(startTag.attributes));
insert(el);
return el;
}

public Parser(TreeBuilder treeBuilder) {
this.treeBuilder = treeBuilder;
settings = treeBuilder.defaultSettings();
}

Token read() {
if (!selfClosingFlagAcknowledged) {
error("Self closing flag not acknowledged");
selfClosingFlagAcknowledged = true;
while (!isEmitPending)
state.read(this, reader);
if (charsBuilder.length() > 0) {
String str = charsBuilder.toString();
charsBuilder.delete(0, charsBuilder.length());
charsString = null;
return charPending.data(str);
} else if (charsString != null) {
Token token = charPending.data(charsString);
charsString = null;
return token;
isEmitPending = false;
return emitPending;
}

void emit(Token token) {
Validate.isFalse(isEmitPending, "There is an unread token pending!");
emitPending = token;
isEmitPending = true;
if (token.type == Token.TokenType.StartTag) {
Token.StartTag startTag = (Token.StartTag) token;
lastStartTag = startTag.tagName;
if (startTag.selfClosing)
selfClosingFlagAcknowledged = false;
} else if (token.type == Token.TokenType.EndTag) {
Token.EndTag endTag = (Token.EndTag) token;
if (endTag.attributes != null)
error("Attributes incorrectly present on end tag");
}

Element insertEmpty(Token.StartTag startTag) {
Tag tag = Tag.valueOf(startTag.name(), settings);
Element el = new Element(tag, baseUri, startTag.attributes);
insertNode(el);
if (startTag.isSelfClosing()) {
if (tag.isKnownTag()) {
if (tag.isSelfClosing()) tokeniser.acknowledgeSelfClosingFlag();
return el;
}

void acknowledgeSelfClosingFlag() {
selfClosingFlagAcknowledged = true;
}

public List<ParseError> getErrors() {
return errors;
}

public Document parseInput(String html, String baseUri) {
errors = isTrackErrors() ? ParseErrorList.tracking(maxErrors) : ParseErrorList.noTracking();
return treeBuilder.parse(new StringReader(html), baseUri, errors, settings);
}

public static Parser htmlParser() {
return new Parser(new HtmlTreeBuilder());
}

public boolean isSelfClosing() {
return empty || selfClosing;
}

protected void initialiseParse(Reader input, String baseUri, ParseErrorList errors, ParseSettings settings) {
super.initialiseParse(input, baseUri, errors, settings);
state = HtmlTreeBuilderState.Initial;
originalState = null;
baseUriSetFromDoc = false;
headElement = null;
formElement = null;
contextElement = null;
formattingElements = new ArrayList<>();
pendingTableCharacters = new ArrayList<>();
emptyEnd = new Token.EndTag();
framesetOk = true;
fosterInserts = false;
fragmentParsing = false;
}

void read(Tokeniser t, CharacterReader r) {
String tagName = r.consumeTagName();
t.tagPending.appendTagName(tagName);
switch (r.consume()) {
t.transition(BeforeAttributeName);
break;
t.transition(SelfClosingStartTag);
break;
t.emitTagPending();
t.transition(Data);
break;
t.eofError(this);
t.transition(Data);
}

public boolean isTrackErrors() {
return maxErrors > 0;
}

void insert(Token.Character characterToken) {
String tagName = currentElement().tagName();
if (tagName.equals("script") || tagName.equals("style"))
node = new TextNode(characterToken.getData(), baseUri);
currentElement().appendChild(node); // doesn't use insertNode, because we don't foster these; and will always have a stack.
}

protected void runParser() {
Token token = tokeniser.read();
process(token);
token.reset();
if (token.type == Token.TokenType.EOF)
break;
}
}

private static boolean isWhitespace(String data) {
for (int i = 0; i < data.length(); i++) {
char c = data.charAt(i);
if (!StringUtil.isWhitespace(c))
return false;
}

final boolean isSelfClosing() {
return selfClosing;
}
