void outerHtmlHead(StringBuilder accum, int depth, Document.OutputSettings out) {
String html = Entities.escape(getWholeText(), out);
if (out.prettyPrint() && parent() instanceof Element && !((Element) parent()).preserveWhitespace()) {
html = normaliseWhitespace(html);
if (out.prettyPrint() && siblingIndex() == 0 && parentNode instanceof Element && ((Element) parentNode).tag().canContainBlock() && !isBlank())
accum.append(html);
}

public void traverse(Node root) {
Node node = root;
int depth = 0;
while (node != null) {
visitor.head(node, depth);
if (node.childNodes().size() > 0) {
node = node.childNode(0);
depth++;
while (node.nextSibling() == null && depth > 0) {
visitor.tail(node, depth);
node = node.parent();
depth--;
visitor.tail(node, depth);
if (node == root)
break;
node = node.nextSibling();
}

void outerHtmlTail(StringBuilder accum, int depth, Document.OutputSettings out) {
if (!(childNodes.isEmpty() && tag.isSelfClosing())) {
if (out.prettyPrint() && !childNodes.isEmpty() && tag.canContainBlock())
indent(accum, depth, out);
accum.append("</").append(tagName()).append(">");
}

private Element addChildToParent(Element child, boolean isEmptyElement) {
Element parent = popStackToSuitableContainer(child.tag());
Tag childTag = child.tag();
boolean validAncestor = stackHasValidParent(childTag);
if (!validAncestor && !relaxed) {
Tag parentTag = childTag.getImplicitParent();
Element implicit = new Element(parentTag, baseUri);
if (child.tag().equals(bodyTag)) {
implicit.appendChild(child);
Element root = addChildToParent(implicit, false);
if (!isEmptyElement)
stack.addLast(child);
return root;
parent.appendChild(child);
if (!isEmptyElement)
stack.addLast(child);
return parent;
}

boolean canContain(Tag child) {
Validate.notNull(child);
if (child.isBlock && !this.canContainBlock)
if (!child.isBlock && !this.canContainInline) // not block == inline
if (this.optionalClosing && this.equals(child))
if (this.empty || this.isData())
if (this.requiresSpecificParent() && this.getImplicitParent().equals(child))
if (limitChildren) {
for (Tag childParent : child.ancestors) {
if (childParent.equals(this))
return true;
if (!excludes.isEmpty()) {
return true;
}

static String escape(String string, CharsetEncoder encoder, EscapeMode escapeMode) {
StringBuilder accum = new StringBuilder(string.length() * 2);
Map<Character, String> map = escapeMode.getMap();
for (int pos = 0; pos < string.length(); pos++) {
Character c = string.charAt(pos);
if (map.containsKey(c))
else if (encoder.canEncode(c))
accum.append(c.charValue());
return accum.toString();
}

public String chompToIgnoreCase(String seq) {
String data = consumeToIgnoreCase(seq); // case insensitive scan
matchChomp(seq);
return data;
}

