}

void read(Tokeniser t, CharacterReader r) {
r.unconsume();
Token.Comment comment = new Token.Comment();
comment.bogus = true;
comment.data.append(r.consumeTo('>'));
t.emit(comment);
t.advanceTransition(Data);
}

void emit(char c) {
