public boolean matchesAny(String... seq) {
for (String s : seq) {
if (matches(s))
return false;
}

public boolean consumeWhitespace() {
boolean seen = false;
while (matchesWhitespace()) {
return seen;
}

public static Elements collect (Evaluator eval, Element root) {
Elements elements = new Elements();
new NodeTraversor(new Accumulator(root, elements, eval)).traverse(root);
return elements;
}

Accumulator(Element root, Elements elements, Evaluator eval) {
this.root = root;
this.elements = elements;
this.eval = eval;
}

public static Document parse(String html, String baseUri) {
TreeBuilder treeBuilder = new HtmlTreeBuilder();
return treeBuilder.parse(html, baseUri, ParseErrorList.noTracking());
}

ParseErrorList(int initialCapacity, int maxSize) {
super(initialCapacity);
this.maxSize = maxSize;
}

static String normaliseWhitespace(String text) {
text = StringUtil.normaliseWhitespace(text);
return text;
}

Document parse(String input, String baseUri, ParseErrorList errors) {
state = HtmlTreeBuilderState.Initial;
return super.parse(input, baseUri, errors);
}

protected boolean process(Token token) {
currentToken = token;
return this.state.process(token, this);
}

void insert(Element el) {
insertNode(el);
stack.add(el);
}

abstract boolean process(Token t, HtmlTreeBuilder tb);
if (t.isCharacter()) {
return false;
}

private boolean anythingElse(Token t, TreeBuilder tb) {
tb.process(new Token.EndTag("head"));
return tb.process(t);
}

protected Node() {
childNodes = Collections.emptyList();
attributes = null;
}

protected void setParentNode(Node parentNode) {
if (this.parentNode != null)
this.parentNode = parentNode;
}

private void reparentChild(Node child) {
if (child.parentNode != null)
child.setParentNode(this);
}

public int size() {
if (attributes == null)
return 0;
}

void advanceTransition(TokeniserState state) {
reader.advance();
this.state = state;
}

Token.Tag createTagPending(boolean start) {
tagPending = start ? new Token.StartTag() : new Token.EndTag();
return tagPending;
}

void emitTagPending() {
tagPending.finaliseTag();
emit(tagPending);
}

boolean isAppropriateEndTagToken() {
if (lastStartTag == null)
return tagPending.tagName.equals(lastStartTag.tagName);
}
