private boolean testValidProtocol(Element el, Attribute attr, Set<Protocol> protocols) {
String value = el.absUrl(attr.getKey());
if (value.length() == 0)
if (!preserveRelativeLinks)
attr.setValue(value);
for (Protocol protocol : protocols) {
String prot = protocol.toString() + ":";
if (value.toLowerCase().startsWith(prot)) {
return true;
}
}

Attributes getEnforcedAttributes(String tagName) {
Attributes attrs = new Attributes();
TagName tag = TagName.valueOf(tagName);
if (enforcedAttributes.containsKey(tag)) {
Map<AttributeKey, AttributeValue> keyVals = enforcedAttributes.get(tag);
for (Map.Entry<AttributeKey, AttributeValue> entry : keyVals.entrySet()) {
attrs.put(entry.getKey().toString(), entry.getValue().toString());
}
return attrs;
}

protected void initialiseParse(String input, String baseUri, ParseErrorList errors) {
Validate.notNull(input, "String input must not be null");
Validate.notNull(baseUri, "BaseURI must not be null");
doc = new Document(baseUri);
reader = new CharacterReader(input);
this.errors = errors;
tokeniser = new Tokeniser(reader, errors);
stack = new DescendableLinkedList<Element>();
this.baseUri = baseUri;
}

private boolean inSpecificScope(String[] targetNames, String[] baseTypes, String[] extraTypes) {
Iterator<Element> it = stack.descendingIterator();
while (it.hasNext()) {
Element el = it.next();
String elName = el.nodeName();
if (StringUtil.in(elName, targetNames))
return true;
if (StringUtil.in(elName, baseTypes))
return false;
}

Element getActiveFormattingElement(String nodeName) {
Iterator<Element> it = formattingElements.descendingIterator();
while (it.hasNext()) {
Element next = it.next();
if (next == null) // scope marker
else if (next.nodeName().equals(nodeName))
return next;
}
return null;
}

private Element findFirstElementByTagName(String tag, Node node) {
if (node.nodeName().equals(tag))
return (Element) node;
for (Node child: node.childNodes) {
Element found = findFirstElementByTagName(tag, child);
if (found != null)
return found;
}
return null;
}

void read(Tokeniser t, CharacterReader r) {
switch (r.current()) {
t.advanceTransition(MarkupDeclarationOpen);
break;
t.advanceTransition(EndTagOpen);
break;
if (r.matchesLetter()) {
t.createTagPending(true);
t.transition(TagName);
}

void read(Tokeniser t, CharacterReader r) {
String tagName = r.consumeToAny('\t', '\n', '\r', '\f', ' ', '/', '>', nullChar).toLowerCase();
t.tagPending.appendTagName(tagName);
switch (r.consume()) {
t.transition(BeforeAttributeName);
break;
t.emitTagPending();
t.transition(Data);
break;
}

void read(Tokeniser t, CharacterReader r) {
String value = r.consumeToAny('\t', '\n', '\r', '\f', ' ', '&', '>', nullChar, '"', '\'', '<', '=', '`');
if (value.length() > 0)
t.tagPending.appendAttributeValue(value);
char c = r.consume();
switch (c) {
t.emitTagPending();
t.transition(Data);
break;
}

void newAttribute() {
if (attributes == null)
if (pendingAttributeName != null) {
if (pendingAttributeValue == null)
attribute = new Attribute(pendingAttributeName, pendingAttributeValue.toString());
attributes.put(attribute);
pendingAttributeName = null;
if (pendingAttributeValue != null)
pendingAttributeValue.delete(0, pendingAttributeValue.length());
}

boolean isSafeAttribute(String tagName, Element el, Attribute attr) {
TagName tag = TagName.valueOf(tagName);
AttributeKey key = AttributeKey.valueOf(attr.getKey());
if (attributes.containsKey(tag)) {
if (attributes.get(tag).contains(key)) {
if (protocols.containsKey(tag)) {
Map<AttributeKey, Set<Protocol>> attrProts = protocols.get(tag);
return !attrProts.containsKey(key) || testValidProtocol(el, attr, attrProts.get(key));
return !tagName.equals(":all") && isSafeAttribute(":all", el, attr);
}

public static Document parseBodyFragment(String bodyHtml, String baseUri) {
Document doc = Document.createShell(baseUri);
Element body = doc.body();
List<Node> nodeList = parseFragment(bodyHtml, body, baseUri);
Node[] nodes = nodeList.toArray(new Node[nodeList.size()]); // the node list gets modified when re-parented
for (Node node : nodes) {
body.appendChild(node);
return doc;
}

