120: @Test public void testGetSiblingsWithDuplicateContent() {
121: Document doc = Jsoup.parse("<div><p>Hello<p id=1>there<p>this<p>this<p>is<p>an<p id=last>element</div>");
122: Element p = doc.getElementById("1");
123: assertEquals("there", p.text());
124: assertEquals("Hello", p.previousElementSibling().text());
125: assertEquals("this", p.nextElementSibling().text());
126: assertEquals("this", p.nextElementSibling().nextElementSibling().text());
127: assertEquals("is", p.nextElementSibling().nextElementSibling().nextElementSibling().text());
128: assertEquals("Hello", p.firstElementSibling().text());
129: assertEquals("element", p.lastElementSibling().text());
130: }
