private void combinator(char combinator) {
tq.consumeWhitespace();
String subQuery = consumeSubQuery(); // support multi > childs
Evaluator newEval = parse(subQuery); // the evaluator to add into target evaluator
boolean replaceRightMost = false;
if (evals.size() == 1) {
rootEval = currentEval = new CombiningEvaluator.And(evals);
evals.clear();
if (combinator == '>')
else if (combinator == ' ')
else if (combinator == '+')
else if (combinator == '~')
else if (combinator == ',') { // group or.
if (currentEval instanceof CombiningEvaluator.Or) {
or = new CombiningEvaluator.Or();
or.add(currentEval);
or.add(newEval);
currentEval = or;
}
if (replaceRightMost)
else rootEval = currentEval;
evals.add(rootEval);
}

private String cacheString(final int start, final int count) {
final char[] val = input;
final String[] cache = stringCache;
if (count > maxCacheLen)
return new String(val, start, count);
int hash = 0;
int offset = start;
for (int i = 0; i < count; i++) {
hash = 31 * hash + val[offset++];
final int index = hash & cache.length - 1;
String cached = cache[index];
if (cached == null) { // miss, add
cached = new String(val, start, count);
cache[index] = cached;
if (rangeEquals(start, count, cached)) { // hit
return cached;
cached = new String(val, start, count);
cache[index] = cached; // update the cache, as recently used strings are more likely to show up again
return cached;
}

public String chompBalanced(char open, char close) {
int start = -1;
int end = -1;
int depth = 0;
char last = 0;
if (isEmpty()) break;
Character c = consume();
if (last == 0 || last != ESC) {
if (c.equals(open)) {
depth++;
if (start == -1)
start = pos;
else if (c.equals(close))
depth--;
if (depth > 0 && last != 0)
end = pos; // don't include the outer match pair in the return
last = c;
} while (depth > 0);
