145: @Test public void testIsValidBodyHtml() {
146: String ok = "<p>Test <b><a href='http://example.com/' rel='nofollow'>OK</a></b></p>";
147: String ok1 = "<p>Test <b><a href='http://example.com/'>OK</a></b></p>"; // missing enforced is OK because still needs run thru cleaner
148: String nok1 = "<p><script></script>Not <b>OK</b></p>";
149: String nok2 = "<p align=right>Test Not <b>OK</b></p>";
150: String nok3 = "<!-- comment --><p>Not OK</p>"; // comments and the like will be cleaned
151: String nok4 = "<html><head>Foo</head><body><b>OK</b></body></html>"; // not body html
152: String nok5 = "<p>Test <b><a href='http://example.com/' rel='nofollowme'>OK</a></b></p>";
153: String nok6 = "<p>Test <b><a href='http://example.com/'>OK</b></p>"; // missing close tag
154: String nok7 = "</div>What";
155: assertTrue(Jsoup.isValid(ok, Whitelist.basic()));
156: assertTrue(Jsoup.isValid(ok1, Whitelist.basic()));
157: assertFalse(Jsoup.isValid(nok1, Whitelist.basic()));
158: assertFalse(Jsoup.isValid(nok2, Whitelist.basic()));
159: assertFalse(Jsoup.isValid(nok3, Whitelist.basic()));
160: assertFalse(Jsoup.isValid(nok4, Whitelist.basic()));
161: assertFalse(Jsoup.isValid(nok5, Whitelist.basic()));
162: assertFalse(Jsoup.isValid(nok6, Whitelist.basic()));
163: assertFalse(Jsoup.isValid(ok, Whitelist.none()));
164: assertFalse(Jsoup.isValid(nok7, Whitelist.basic()));
165: }
