public String consumeAttributeKey() {
int start = pos;
while (!isEmpty() && (matchesWord() || matchesAny('-', '_', ':')))
pos++;
return queue.substring(start, pos);
}

protected void addChildren(Node... children) {
for (Node child: children) {
reparentChild(child);
childNodes.add(child);
child.setSiblingIndex(childNodes.size()-1);
}

private Entities.EscapeMode escapeMode = Entities.EscapeMode.base;
private Charset charset = Charset.forName("UTF-8");
private CharsetEncoder charsetEncoder = charset.newEncoder();
private boolean prettyPrint = true;
private int indentAmount = 1;
public OutputSettings() {}

public Attribute(String key, String value) {
Validate.notEmpty(key);
Validate.notNull(value);
this.key = key.trim().toLowerCase();
this.value = value;
}

private Element popStackToSuitableContainer(Tag tag) {
while (!stack.isEmpty()) {
if (last().tag().canContain(tag))
return last();
}

private Tag setContainInlineOnly() {
canContainBlock = false;
canContainInline = true;
return this;
}

private Tag setParent(String... tagNames) {
directDescendant = true;
setAncestor(tagNames);
return this;
}

public Character consume() {
Character c = queue.charAt(pos);
pos++;
return c;
}

public String chompTo(String seq) {
String data = consumeTo(seq);
matchChomp(seq);
return data;
}

public String chompToIgnoreCase(String seq) {
String data = consumeToIgnoreCase(seq); // case insensitive scan
matchChomp(seq);
return data;
}

public Element(Tag tag, String baseUri, Attributes attributes) {
super(baseUri, attributes);
Validate.notNull(tag);
this.tag = tag;
}

public Element appendChild(Node child) {
Validate.notNull(child);
addChildren(child);
return this;
}

public XmlDeclaration(String data, String baseUri, boolean isProcessingInstruction) {
super(baseUri);
attributes.put(DECL_KEY, data);
this.isProcessingInstruction = isProcessingInstruction;
}

public static Document parse(String html, String baseUri) {
Parser parser = new Parser(html, baseUri, false);
return parser.parse();
}

private Tag setCanContainBlock() {
canContainBlock = true;
return this;
}

private Tag setOptionalClosing() {
optionalClosing = true;
return this;
}

private Tag setPreserveWhitespace() {
preserveWhitespace = true;
return this;
}

private Tag setLimitChildren() {
limitChildren = true;
return this;
}

private Tag setKnownTag() {
knownTag = true;
return this;
