private Element findFirstElementByTagName(String tag, Node node) {
if (node.nodeName().equals(tag))
return (Element) node;
for (Node child: node.childNodes) {
Element found = findFirstElementByTagName(tag, child);
if (found != null)
return found;
}
return null;
}

static String escape(String string, CharsetEncoder encoder, EscapeMode escapeMode) {
StringBuilder accum = new StringBuilder(string.length() * 2);
Map<Character, String> map = escapeMode.getMap();
for (int pos = 0; pos < string.length(); pos++) {
Character c = string.charAt(pos);
if (map.containsKey(c))
else if (encoder.canEncode(c))
accum.append(c.charValue());
return accum.toString();
}

private void parseEndTag() {
tq.consume("</");
String tagName = tq.consumeTagName();
tq.chompTo(">");
if (tagName.length() != 0) {
Tag tag = Tag.valueOf(tagName);
if (!last().tag().isIgnorableEndTag(tag)) // skips </tr> if in <table>
popStackToClose(tag);
}

public Node nextSibling() {
if (parentNode == null)
List<Node> siblings = parentNode.childNodes;
Integer index = siblingIndex();
Validate.notNull(index);
if (siblings.size() > index+1)
return siblings.get(index+1);
return null;
}

public boolean matches(String seq) {
int count = seq.length();
if (count > remainingLength())
while (--count >= 0) {
if (Character.toLowerCase(seq.charAt(count)) != Character.toLowerCase(queue.charAt(pos+count)))
return false;
return true;
}

private static Tag register(Tag tag) {
tag.setAncestor(defaultAncestor.tagName);
tag.setKnownTag();
synchronized (tags) {
tags.put(tag.tagName, tag);
}
return tag;
