protected void removeChild(Node out) {
Validate.isTrue(out.parentNode == this);
int index = indexInList(out, childNodes);
childNodes.remove(index);
out.parentNode = null;
}

static String stripLeadingWhitespace(String text) {
return text.replaceFirst("^\\s+", "");
}

protected static <N extends Node> Integer indexInList(N search, List<N> nodes) {
Validate.notNull(search);
Validate.notNull(nodes);
for (int i = 0; i < nodes.size(); i++) {
N node = nodes.get(i);
if (node.equals(search))
return i;
}

static boolean lastCharIsWhitespace(StringBuilder sb) {
if (sb.length() == 0)
return false;
String lastChar = sb.substring(sb.length()-1, sb.length());
Validate.isTrue(lastChar.length() == 1); // todo: remove check
return lastChar.equals(" ");
}

public boolean isBlank() {
return StringUtils.isBlank(normaliseWhitespace(getWholeText()));
}

public String text() {
StringBuilder sb = new StringBuilder();
text(sb);
return sb.toString().trim();
}

private void text(StringBuilder accum) {
for (Node child : childNodes) {
if (child instanceof TextNode) {
TextNode textNode = (TextNode) child;
String text = textNode.getWholeText();
if (!preserveWhitespace()) {
text = TextNode.normaliseWhitespace(text);
if (TextNode.lastCharIsWhitespace(accum))
text = TextNode.stripLeadingWhitespace(text);
accum.append(text);
} else if (child instanceof Element) {
Element element = (Element) child;
if (accum.length() > 0 && element.isBlock() && !TextNode.lastCharIsWhitespace(accum))
element.text(accum);
}
}

public boolean equals(Object o) {
if (this == o) return true;
}

public boolean preserveWhitespace() {
return preserveWhitespace;
}

boolean preserveWhitespace() {
return tag.preserveWhitespace() || parent() != null && parent().preserveWhitespace();
}

static String normaliseWhitespace(String text) {
text = text.replaceAll("\\s{2,}|(\\r\\n|\\r|\\n)", " "); // more than one space, and newlines to " "
return text;
}

public String getWholeText() {
return attributes.get(TEXT_KEY);
}

public Node parent() {
return parentNode;
}

public Element parent() {
return (Element) super.parent();
}

private void parseTextNode() {
String text = tq.consumeTo("<");
TextNode textNode = TextNode.createFromEncoded(text, baseUri);
last().appendChild(textNode);
}

public static TextNode createFromEncoded(String encodedText, String baseUri) {
String text = StringEscapeUtils.unescapeHtml(encodedText);
return new TextNode(text, baseUri);
}

private void parseEndTag() {
tq.consume("</");
String tagName = tq.consumeWord();
tq.chompTo(">");
if (tagName.length() != 0) {
Tag tag = Tag.valueOf(tagName);
popStackToClose(tag);
}

private Element popStackToClose(Tag tag) {
int counter = 0;
Element elToClose = null;
for (int i = stack.size() -1; i > 0; i--) {
counter++;
Element el = stack.get(i);
Tag elTag = el.tag();
if (elTag.equals(bodyTag) || elTag.equals(htmlTag)) { // once in body, don't close past body
} else if (elTag.equals(tag)) {
elToClose = el;
break;
if (elToClose != null) {
for (int i = 0; i < counter; i++) {
stack.removeLast();
return elToClose;
}

Tag getImplicitParent() {
return (!ancestors.isEmpty()) ? ancestors.get(0) : null;
}

public TextNode(String text, String baseUri) {
super(baseUri);
attributes.put(TEXT_KEY, text);
}
