}

String consumeToAny(char... seq) {
int start = pos;
OUTER: while (!isEmpty()) {
char c = input.charAt(pos);
for (char seek : seq) {
if (seek == c)
break OUTER;
pos++;
}
return pos > start ? input.substring(start, pos) : "";
}

void transition(TokeniserState state) {
this.state = state;
}

void advanceTransition(TokeniserState state) {
reader.advance();
this.state = state;
}

