void read(Tokeniser t, CharacterReader r) {
String value = r.consumeToAny('\t', '\n', '\f', ' ', '&', '>', nullChar, '"', '\'', '<', '=', '`');
if (value.length() > 0)
t.tagPending.appendAttributeValue(value);
char c = r.consume();
switch (c) {
t.emitTagPending();
t.transition(Data);
break;
}

private void combinator(char combinator) {
tq.consumeWhitespace();
String subQuery = consumeSubQuery(); // support multi > childs
if (evals.size() == 1)
e = evals.get(0);
evals.clear();
Evaluator f = parse(subQuery);
if (combinator == '>')
evals.add(new CombiningEvaluator.And(f, new StructuralEvaluator.ImmediateParent(e)));
else if (combinator == ' ')
else if (combinator == '+')
evals.add(new CombiningEvaluator.And(f, new StructuralEvaluator.ImmediatePreviousSibling(e)));
}

private String consumeSubQuery() {
StringBuilder sq = new StringBuilder();
while (!tq.isEmpty()) {
if (tq.matches("("))
else if (tq.matches("["))
else if (tq.matchesAny(combinators))
break;
sq.append(tq.consume());
return sq.toString();
}

private void byClass() {
String className = tq.consumeCssIdentifier();
Validate.notEmpty(className);
evals.add(new Evaluator.Class(className.trim().toLowerCase()));
}

public String consumeCssIdentifier() {
int start = pos;
while (!isEmpty() && (matchesWord() || matchesAny('-', '_')))
pos++;
return queue.substring(start, pos);
}

