CharacterReader(String input) {
Validate.notNull(input);
this.input = input.toCharArray();
this.length = this.input.length;
}

boolean isEmpty() {
return pos >= length;
}

char current() {
return isEmpty() ? EOF : input[pos];
}

void unconsume() {
pos--;
}

void advance() {
pos++;
}

void mark() {
mark = pos;
}

String consumeToAny(final char... chars) {
int start = pos;
OUTER: while (pos < length) {
for (int i = 0; i < chars.length; i++) {
if (input[pos] == chars[i])
break OUTER;
pos++;
return pos > start ? new String(input, start, pos-start) : "";
}

String consumeLetterThenDigitSequence() {
int start = pos;
while (pos < length) {
char c = input[pos];
if ((c >= 'A' && c <= 'Z') || (c >= 'a' && c <= 'z'))
pos++;
}
while (!isEmpty()) {
char c = input[pos];
if (c >= '0' && c <= '9')
return new String(input, start, pos - start);
}

boolean matches(char c) {
return !isEmpty() && input[pos] == c;
}

boolean matches(String seq) {
int scanLength = seq.length();
if (scanLength > length - pos)
for (int offset = 0; offset < scanLength; offset++)
if (seq.charAt(offset) != input[pos+offset])
return false;
return true;
}

boolean matchesAny(char... seq) {
if (isEmpty())
char c = input[pos];
for (char seek : seq) {
if (seek == c)
return true;
return false;
}

boolean matchConsume(String seq) {
if (matches(seq)) {
pos += seq.length();
return true;
return false;
}

Tokeniser(CharacterReader reader, ParseErrorList errors) {
this.reader = reader;
this.errors = errors;
}

Token read() {
if (!selfClosingFlagAcknowledged) {
while (!isEmitPending)
state.read(this, reader);
if (charBuffer.length() > 0) {
String str = charBuffer.toString();
charBuffer.delete(0, charBuffer.length());
return new Token.Character(str);
isEmitPending = false;
return emitPending;
}
