@Test public void strictAttributeUnescapes() {
String html = "<a id=1 href='?foo=bar&mid&lt=true'>One</a> <a id=2 href='?foo=bar&lt;qux&lg=1'>Two</a>";
Elements els = Jsoup.parse(html).select("a");
