public Document(String baseUri) {
super(Tag.valueOf("#root"), baseUri);
}

static public Document createShell(String baseUri) {
Validate.notNull(baseUri);
Document doc = new Document(baseUri);
Element html = doc.appendElement("html");
html.appendElement("head");
html.appendElement("body");
return doc;
}

public Element body() {
return findFirstElementByTagName("body", this);
}

private Element findFirstElementByTagName(String tag, Node node) {
if (node.nodeName().equals(tag))
return (Element) node;
for (Node child: node.childNodes) {
Element found = findFirstElementByTagName(tag, child);
if (found != null)
return found;
}
return null;
}

public String nodeName() {
return "#document";
}

public QuirksMode quirksMode() {
return quirksMode;
}

public Document quirksMode(QuirksMode quirksMode) {
this.quirksMode = quirksMode;
return this;
}

public String getName() {
return tagName;
}

public static Tag valueOf(String tagName) {
Validate.notNull(tagName);
Tag tag = tags.get(tagName);
if (tag == null) {
tagName = tagName.trim().toLowerCase();
Validate.notEmpty(tagName);
tag = tags.get(tagName);
if (tag == null) {
tag = new Tag(tagName);
tag.isBlock = false;
tag.canContainBlock = true;
return tag;
}

private static void register(Tag tag) {
tags.put(tag.tagName, tag);
}

public String get(String key) {
Validate.notEmpty(key);
if (attributes == null)
Attribute attr = attributes.get(key.toLowerCase());
return attr != null ? attr.getValue() : "";
}

public void put(String key, String value) {
Attribute attr = new Attribute(key, value);
put(attr);
}

public void put(Attribute attribute) {
Validate.notNull(attribute);
if (attributes == null)
attributes = new LinkedHashMap<String, Attribute>(2);
attributes.put(attribute.getKey(), attribute);
}

public boolean hasKey(String key) {
return attributes != null && attributes.containsKey(key.toLowerCase());
}

public int size() {
if (attributes == null)
return 0;
return attributes.size();
}

public void addAll(Attributes incoming) {
if (incoming.size() == 0)
return;
if (attributes == null)
attributes.putAll(incoming.attributes);
}

public Iterator<Attribute> iterator() {
return asList().iterator();
}

public List<Attribute> asList() {
if (attributes == null)
return Collections.emptyList();
List<Attribute> list = new ArrayList<Attribute>(attributes.size());
for (Map.Entry<String, Attribute> entry : attributes.entrySet()) {
list.add(entry.getValue());
}
return Collections.unmodifiableList(list);
}

