switch (c) {
t.transition(BeforeAttributeName);
break;
}

void read(Tokeniser t, CharacterReader r) {
char c = r.consume();
switch (c) {
t.tagPending.selfClosing = true;
t.emitTagPending();
t.transition(Data);
break;
}

