public Attribute(String key, String value) {
Validate.notEmpty(key);
Validate.notNull(value);
this.key = key.trim().toLowerCase();
this.value = value;
}

public String getKey() {
return key;
}

public String getValue() {
return value;
}

public static String padding(int width) {
if (width < 0)
if (width < padding.length)
return padding[width];
}

public static boolean isBlank(String string) {
if (string == null || string.length() == 0)
int l = string.length();
for (int i = 0; i < l; i++) {
if (!StringUtil.isWhitespace(string.codePointAt(i)))
return false;
}

public static boolean isWhitespace(int c){
return c == ' ' || c == '\t' || c == '\n' || c == '\f' || c == '\r';
}

public TextNode(String text, String baseUri) {
this.baseUri = baseUri;
this.text = text;
}

public String nodeName() {
return "#text";
}

public String getWholeText() {
return attributes == null ? text : attributes.get(TEXT_KEY);
}

public boolean isBlank() {
return StringUtil.isBlank(getWholeText());
}

void outerHtmlHead(Appendable accum, int depth, Document.OutputSettings out) throws IOException {
if (out.prettyPrint() && ((siblingIndex() == 0 && parentNode instanceof Element && ((Element) parentNode).tag().formatAsBlock() && !isBlank()) || (out.outline() && siblingNodes().size()>0 && !isBlank()) ))
indent(accum, depth, out);
boolean normaliseWhite = out.prettyPrint() && parent() instanceof Element
Entities.escape(accum, getWholeText(), out, false, normaliseWhite, false);
}

public NodeTraversor(NodeVisitor visitor) {
this.visitor = visitor;
}

public void traverse(Node root) {
Node node = root;
int depth = 0;
while (node != null) {
visitor.head(node, depth);
if (node.childNodeSize() > 0) {
node = node.childNode(0);
depth++;
while (node.nextSibling() == null && depth > 0) {
visitor.tail(node, depth);
node = node.parentNode();
depth--;
visitor.tail(node, depth);
if (node == root)
break;
node = node.nextSibling();
}

OuterHtmlVisitor(Appendable accum, Document.OutputSettings out) {
this.accum = accum;
this.out = out;
}

public void head(Node node, int depth) {
node.outerHtmlHead(accum, depth, out);
}
}

public void tail(Node node, int depth) {
if (!node.nodeName().equals("#text")) { // saves a void hit.
node.outerHtmlTail(accum, depth, out);
}
}

private static CoreCharset byName(String name) {
if (name.equals("US-ASCII"))
if (name.startsWith("UTF-")) // covers UTF-8, UTF-16, et al
return utf;
return fallback;
}

private static boolean canEncode(final CoreCharset charset, final char c, final CharsetEncoder fallback) {
switch (charset) {
return true; // real is:!(Character.isLowSurrogate(c) || Character.isHighSurrogate(c)); - but already check above
return fallback.canEncode(c);
}
