protected void runParser() {
Token token = tokeniser.read();
process(token);
token.reset();
if (token.type == Token.TokenType.EOF)
break;
}
}

protected Element currentElement() {
int size = stack.size();
return size > 0 ? stack.get(size-1) : null;
}

