public String normalizeTag(String name) {
name = name.trim();
if (!preserveTagCase)
name = lowerCase(name);
return name;
}

public String normalizeAttribute(String name) {
name = name.trim();
if (!preserveAttributeCase)
name = lowerCase(name);
return name;
}

Attributes normalizeAttributes(Attributes attributes) {
if (!preserveAttributeCase) {
attributes.normalize();
return attributes;
}

ParseErrorList(int initialCapacity, int maxSize) {
super(initialCapacity);
this.maxSize = maxSize;
}

boolean canAddError() {
return size() < maxSize;
}

public static ParseErrorList noTracking() {
return new ParseErrorList(0, 0);
}

public static void notNull(Object obj) {
if (obj == null)
}

public static void notNull(Object obj, String msg) {
if (obj == null)
}

public static void isTrue(boolean val) {
if (!val)
}

public static void isTrue(boolean val, String msg) {
if (!val)
}

public static void isFalse(boolean val) {
if (val)
}

public static void isFalse(boolean val, String msg) {
if (val)
}

public static void notEmpty(String string) {
if (string == null || string.length() == 0)
}

public String attr(String attributeKey) {
Validate.notNull(attributeKey);
if (!hasAttributes())
String val = attributes().getIgnoreCase(attributeKey);
if (val.length() > 0)
return val;
}

public abstract Attributes attributes();
attributeKey = getParser().settings().normalizeAttribute(attributeKey);
attributes().putIgnoreCase(attributeKey, attributeValue);
return this;
}

public Node childNode(int index) {
return ensureChildNodes().get(index);
}

public final Node parentNode() {
return parentNode;
}

public Node root() {
Node node = this;
while (node.parentNode != null)
return node;
}

public Document ownerDocument() {
Node root = root();
return (root instanceof Document) ? (Document) root : null;
}
