formattingElements = new ArrayList<>();
pendingTableCharacters = new ArrayList<>();
emptyEnd = new Token.EndTag();
framesetOk = true;
fosterInserts = false;
fragmentParsing = false;
}

protected boolean process(Token token) {
currentToken = token;
return this.state.process(token, this);
}

