protected void setParentNode(Node parentNode) {
if (this.parentNode != null)
this.parentNode = parentNode;
}

protected void removeChild(Node out) {
Validate.isTrue(out.parentNode == this);
int index = indexInList(out, childNodes);
childNodes.remove(index);
out.parentNode = null;
}

protected static <N extends Node> Integer indexInList(N search, List<N> nodes) {
Validate.notNull(search);
Validate.notNull(nodes);
for (int i = 0; i < nodes.size(); i++) {
N node = nodes.get(i);
if (node.equals(search))
return i;
}

public boolean equals(Object o) {
if (this == o) return true;
}

public int hashCode() {
int result = parentNode != null ? parentNode.hashCode() : 0;
result = 31 * result + (attributes != null ? attributes.hashCode() : 0);
return result;
}

public Element(Tag tag, String baseUri, Attributes attributes) {
super(baseUri, attributes);
Validate.notNull(tag);
this.tag = tag;
}

public Element(Tag tag, String baseUri) {
this(tag, baseUri, new Attributes());
}

public Tag tag() {
return tag;
}

public Element parent() {
return (Element) super.parent();
}

public Elements children() {
List<Element> elements = new ArrayList<Element>();
for (Node node : childNodes) {
if (node instanceof Element)
elements.add((Element) node);
}
return new Elements(elements);
}

public Elements select(String query) {
return Selector.select(query, this);
}

public Element appendChild(Node child) {
Validate.notNull(child);
child.setParentNode(this);
childNodes.add(child);
return this;
}

public Elements getElementsByTag(String tagName) {
Validate.notEmpty(tagName);
tagName = tagName.toLowerCase().trim();
return Collector.collect(new Evaluator.Tag(tagName), this);
}

public String text() {
StringBuilder sb = new StringBuilder();
text(sb);
return sb.toString().trim();
}

private void text(StringBuilder accum) {
for (Node child : childNodes) {
if (child instanceof TextNode) {
TextNode textNode = (TextNode) child;
String text = textNode.getWholeText();
if (!preserveWhitespace()) {
text = TextNode.normaliseWhitespace(text);
if (TextNode.lastCharIsWhitespace(accum))
text = TextNode.stripLeadingWhitespace(text);
accum.append(text);
} else if (child instanceof Element) {
Element element = (Element) child;
if (accum.length() > 0 && element.isBlock() && !TextNode.lastCharIsWhitespace(accum))
element.text(accum);
}
}

boolean preserveWhitespace() {
return tag.preserveWhitespace() || parent() != null && parent().preserveWhitespace();
}

public int hashCode() {
int result = super.hashCode();
result = 31 * result + (tag != null ? tag.hashCode() : 0);
return result;
}

public String get(String key) {
Validate.notEmpty(key);
Attribute attr = attributes.get(key.toLowerCase());
return attr != null ? attr.getValue() : "";
}

public void put(String key, String value) {
Attribute attr = new Attribute(key, value);
put(attr);
}

public void put(Attribute attribute) {
Validate.notNull(attribute);
attributes.put(attribute.getKey(), attribute);
}
