public Document parseInput(String html, String baseUri) {
errors = isTrackErrors() ? ParseErrorList.tracking(maxErrors) : ParseErrorList.noTracking();
return treeBuilder.parse(html, baseUri, errors, settings);
}

public Element(Tag tag, String baseUri) {
this(tag, baseUri, new Attributes());
}

private Element findFirstElementByTagName(String tag, Node node) {
if (node.nodeName().equals(tag))
return (Element) node;
for (Node child: node.childNodes) {
Element found = findFirstElementByTagName(tag, child);
if (found != null)
return found;
}
return null;
}

void emit(final String str) {
if (charsString == null) {
charsString = str;
}

void advanceTransition(TokeniserState state) {
reader.advance();
this.state = state;
}

final boolean isStartTag() {
return type == TokenType.StartTag;
}

protected void ensureChildNodes() {
if (childNodes == EMPTY_NODES) {
childNodes = new NodeList(4);
}

public boolean prettyPrint() {
return prettyPrint;
}

String consumeTagName() {
final int start = pos;
final int remaining = length;
final char[] val = input;
while (pos < remaining) {
final char c = val[pos];
if (c == '\t'|| c ==  '\n'|| c ==  '\r'|| c ==  '\f'|| c ==  ' '|| c ==  '/'|| c ==  '>'|| c ==  TokeniserState.nullChar)
pos++;
}
return pos > start ? cacheString(start, pos-start) : "";
}

public static final ParseSettings htmlDefault;
htmlDefault = new ParseSettings(false, false);
preserveCase = new ParseSettings(true, true);
}

public String nodeName() {
return "#document";
}

public Element body() {
return findFirstElementByTagName("body", this);
}

final boolean isSelfClosing() {
return selfClosing;
}

Character data(String data) {
this.data = data;
return this;
}

protected Node() {
childNodes = EMPTY_NODES;
attributes = null;
}

protected void outerHtml(Appendable accum) {
new NodeTraversor(new OuterHtmlVisitor(accum, getOutputSettings())).traverse(this);
}

public Element appendChild(Node child) {
Validate.notNull(child);
reparentChild(child);
ensureChildNodes();
childNodes.add(child);
child.setSiblingIndex(childNodes.size() - 1);
return this;
}

final String name() { // preserves case, for input into Tag.valueOf (which may drop case)
Validate.isFalse(tagName == null || tagName.length() == 0);
return tagName;
}

void outerHtmlHead(Appendable accum, int depth, Document.OutputSettings out) throws IOException {
if (out.prettyPrint() && ((siblingIndex() == 0 && parentNode instanceof Element && ((Element) parentNode).tag().formatAsBlock() && !isBlank()) || (out.outline() && siblingNodes().size()>0 && !isBlank()) ))
indent(accum, depth, out);
boolean normaliseWhite = out.prettyPrint() && parent() instanceof Element
Entities.escape(accum, getWholeText(), out, false, normaliseWhite, false);
}
