439: public void testFitAccuracyDependsOnBoundary() {
440: final CMAESOptimizer optimizer = new CMAESOptimizer();
441: final MultivariateFunction fitnessFunction = new MultivariateFunction() {
442: public double value(double[] parameters) {
443: final double target = 11.1;
444: final double error = target - parameters[0];
445: return error * error;
446: }
447: };
448: 449: final double[] start = { 1 };
450: 451: // No bounds.
452: PointValuePair result = optimizer.optimize(100000, fitnessFunction, GoalType.MINIMIZE,
453: start);
454: final double resNoBound = result.getPoint()[0];
455: 456: // Optimum is near the lower bound.
457: final double[] lower = { -20 };
458: final double[] upper = { 5e16 };
459: result = optimizer.optimize(100000, fitnessFunction, GoalType.MINIMIZE,
460: start, lower, upper);
461: final double resNearLo = result.getPoint()[0];
462: 463: // Optimum is near the upper bound.
464: lower[0] = -5e16;
465: upper[0] = 20;
466: result = optimizer.optimize(100000, fitnessFunction, GoalType.MINIMIZE,
467: start, lower, upper);
468: final double resNearHi = result.getPoint()[0];
