472: //                    " resNearHi=" + resNearHi);
473: 474: // The two values currently differ by a substantial amount, indicating that
475: // the bounds definition can prevent reaching the optimum.
476: Assert.assertEquals(resNoBound, resNearLo, 1e-3);
477: Assert.assertEquals(resNoBound, resNearHi, 1e-3);
478: }

