double norm2 = 0;
for (int i = 0; i < weightedResidualJacobian.length; ++i) {
double akk = weightedResidualJacobian[i][k];
norm2 += akk * akk;
jacNorm[k] = FastMath.sqrt(norm2);
for (int k = 0; k < cols; ++k) {
int nextColumn = -1;
double ak2 = Double.NEGATIVE_INFINITY;
