private static int computeMSB(final long l) {
long ll = l;
long mask  = 0xffffffffL;
int  scale = 32;
int  msb   = 0;
while (scale != 0) {
if ((ll & mask) != ll) {
msb |= scale;
ll = ll >> scale;
scale = scale >> 1;
mask  = mask >> scale;
return msb;
}

private static int computeLSB(final long l) {
long ll = l;
long mask  = 0xffffffff00000000L;
int  scale = 32;
int  lsb   = 0;
while (scale != 0) {
if ((ll & mask) == ll) {
lsb |= scale;
ll = ll >> scale;
scale = scale >> 1;
mask  = mask >> scale;
return lsb;
}

private int getBit(final int i, final int k) {
final long bits = Double.doubleToLongBits(components[i]);
final int e = exponent(bits);
if ((k < e) || (k > offset)) {
return 0;
