for (int i = 0; i < buf.length; i++) {
buf[i] = a.charAt(i);
if (buf[i] >= '1' && buf[i] <= '9') {
leading = false;
if (buf[i] == '.') {
sig += (400 - sig) % 4;
leading = false;
if (sig == (radixDigits / 2) * 4) {
sp = i;
break;
if (buf[i] >= '0' && buf[i] <= '9' && !leading) {
sig ++;
result[0] = new Dfp(this, new String(buf, 0, sp));
for (int i = 0; i < buf.length; i++) {
buf[i] = a.charAt(i);
if (buf[i] >= '0' && buf[i] <= '9' && i < sp) {
buf[i] = '0';
result[1] = new Dfp(this, new String(buf));
return result;
}

