double sum = 0;
for (int i = j + 1; i < nSing; ++i) {
sum += weightedResidualJacobian[i][pj] * work[i];
work[j] = (work[j] - sum) / lmDiag[j];
for (int j = 0; j < lmDir.length; ++j) {
