public CGLIBProxyRealMethod(MockitoMethodProxy methodProxy) {
this.methodProxy = methodProxy;
}

public static synchronized int next() {
return sequenceNumber++;
}

public Object handle(Invocation invocation) throws Throwable {
if (invocationContainerImpl.hasAnswersForStubbing()) {
VerificationMode verificationMode = mockingProgress.pullVerificationMode();
InvocationMatcher invocationMatcher = matchersBinder.bindMatchers(mockingProgress.getArgumentMatcherStorage(),
mockingProgress.validateState();
if (verificationMode != null) {
if (((MockAwareVerificationMode) verificationMode).getMock() == invocation.getMock()) {
VerificationDataImpl data = new VerificationDataImpl(invocationContainerImpl, invocationMatcher);
verificationMode.verify(data);
return null;
invocationContainerImpl.setInvocationForPotentialStubbing(invocationMatcher);
OngoingStubbingImpl<T> ongoingStubbing = new OngoingStubbingImpl<T>(invocationContainerImpl);
mockingProgress.reportOngoingStubbing(ongoingStubbing);
StubbedInvocationMatcher stubbedInvocation = invocationContainerImpl.findAnswerFor(invocation);
if (stubbedInvocation != null) {
Object ret = mockSettings.getDefaultAnswer().answer(invocation);
invocationContainerImpl.resetInvocationForPotentialStubbing(invocationMatcher);
return ret;
}

public boolean hasAnswersForStubbing() {
return !answersForStubbing.isEmpty();
}

public FilteredCGLIBProxyRealMethod(RealMethod realMethod) {
this.realMethod = realMethod;
}

public MockitoMethodProxy createMockitoMethodProxy(MethodProxy methodProxy) {
if (mockSettings.isSerializable())
return new DelegatingMockitoMethodProxy(methodProxy);
}

public ArgumentMatcherStorage getArgumentMatcherStorage() {
return threadSafely().getArgumentMatcherStorage();
}

public DelegatingMethod(Method method) {
assert method != null : "Method cannot be null";
this.method = method;
}

public MockitoAssertionError(String message) {
super(message);
unfilteredStackTrace = getStackTrace();
ConditionalStackTraceFilter filter = new ConditionalStackTraceFilter();
filter.filter(this);
}

public boolean isHashCodeMethod(Method method) {
return method.getName().equals("hashCode") && method.getParameterTypes().length == 0;
}

public Method getMethod() {
return method.getJavaMethod();
}

public Object intercept(Object proxy, Method method, Object[] args, MethodProxy methodProxy)
if (objectMethodsGuru.isEqualsMethod(method)) {
return proxy == args[0];
} else if (objectMethodsGuru.isHashCodeMethod(method)) {
MockitoMethodProxy mockitoMethodProxy = createMockitoMethodProxy(methodProxy);
cglibHacker.setMockitoNamingPolicy(mockitoMethodProxy);
MockitoMethod mockitoMethod = createMockitoMethod(method);
FilteredCGLIBProxyRealMethod realMethod = new FilteredCGLIBProxyRealMethod(mockitoMethodProxy);
Invocation invocation = new Invocation(proxy, mockitoMethod, args, SequenceNumber.next(), realMethod);
return handler.handle(invocation);
}

public boolean isEqualsMethod(Method method) {
return method.getName().equals("equals") && method.getParameterTypes().length == 1 && method.getParameterTypes()[0] == Object.class;
}

protected List<Matcher> argumentsToMatchers() {
List<Matcher> matchers = new ArrayList<Matcher>(arguments.length);
for (Object arg : arguments) {
return matchers;
}

protected String toString(List<Matcher> matchers, PrintSettings printSettings) {
MatchersPrinter matchersPrinter = new MatchersPrinter();
String method = qualifiedMethodName();
String invocation = method + matchersPrinter.getArgumentsLine(matchers, printSettings);
if (printSettings.isMultiline() || (!matchers.isEmpty() && invocation.length() > MAX_LINE_LENGTH)) {
return invocation;
}

private String qualifiedMethodName() {
return new MockUtil().getMockName(mock) + "." + method.getName();
}

public boolean isMultiline() {
return multiline;
}

private Class<?>[] prepend(Class<?> first, Class<?>... rest) {
Class<?>[] all = new Class<?>[rest.length+1];
all[0] = first;
System.arraycopy(rest, 0, all, 1, rest.length);
return all;
}

public String getArgumentsLine(List<Matcher> matchers, PrintSettings printSettings) {
Description result = new StringDescription();
result.appendList("(", ", ", ");", applyPrintSettings(matchers, printSettings));
return result.toString();
}

public Location getLocation() {
return location;
