public boolean argumentsMatch(InvocationMatcher invocationMatcher, Invocation actual) {
Object[] actualArgs = actual.getArguments();
return argumentsMatch(invocationMatcher, actualArgs) || varArgsMatch(invocationMatcher, actual);
}

private static Object[] expandVarArgs(final boolean isVarArgs, final Object[] args) {
if (!isVarArgs || args[args.length - 1] != null && !args[args.length - 1].getClass().isArray()) {
return args == null ? new Object[0] : args;
}

public MockName(String mockName, Class classToMock) {
if (mockName == null) {
this.mockName = mockName;
}

public void enableAccess(Field field) {
wasAccessible = field.isAccessible();
field.setAccessible(true);
}

public List<Matcher> pullMatchers() {
if (matcherStack.isEmpty()) {
return Collections.emptyList();
}

public void mockingStarted(Object mock, Class classToMock, MockSettings mockSettings) {
if (listener != null && listener instanceof MockingStartedListener) {
validateState();
}

static MockingProgress threadSafely() {
if (mockingProgress.get() == null) {
return mockingProgress.get();
}

public void removeLast() {
int last = invocations.size() - 1;
invocations.remove(last);
}

public MockAwareVerificationMode(Object mock, VerificationMode mode) {
this.mock = mock;
this.mode = mode;
}

public MethodInterceptorFilter(MockitoInvocationHandler handler, MockSettingsImpl mockSettings) {
