assertTrue(areEqual(new String[0], new String[0]));
assertTrue(areEqual(new Object[10], new Object[10]));
assertTrue(areEqual(new int[] {1}, new Integer[] {1}));
assertTrue(areEqual(new Object[] {"1"}, new String[] {"1"}));
