public MockSettings name(String name) {
this.name = name;
return this;
}

private static Object[] expandVarArgs(final boolean isVarArgs, final Object[] args) {
if (!isVarArgs || args[args.length - 1] != null && !args[args.length - 1].getClass().isArray()) {
return args == null ? new Object[0] : args;
}

public MockName(String mockName, Class classToMock) {
if (mockName == null) {
this.mockName = mockName;
}

public HandyReturnValues reportMatcher(Matcher matcher) {
matcherStack.push(new LocalizedMatcher(matcher));
return new HandyReturnValues();
}

public DelegatingMethod(Method method) {
assert method != null : "Method cannot be null";
this.method = method;
}

public List<Matcher> pullMatchers() {
if (matcherStack.isEmpty()) {
return Collections.emptyList();
}

public MockitoMethod createMockitoMethod(Method method) {
if (mockSettings.isSerializable()) {
return new DelegatingMethod(method);
}

public MethodInterceptorFilter(IMockHandler mockHandler, MockSettingsImpl mockSettings) {
this.mockHandler = mockHandler;
this.mockSettings = mockSettings;
}

static MockingProgress threadSafely() {
if (mockingProgress.get() == null) {
return mockingProgress.get();
}

public LocalizedMatcher(Matcher actualMatcher) {
this.actualMatcher = actualMatcher;
this.location = new Location();
}

private static void addIfNewElement(List<ClassLoader> loaders, ClassLoader c) {
if (c != null && !loaders.contains(c)) {
loaders.add(c);
}

public MockitoMethodProxy createMockitoMethodProxy(MethodProxy methodProxy) {
if (mockSettings.isSerializable())
return new DelegatingMockitoMethodProxy(methodProxy);
}

public static <T> T isA(Class<T> clazz) {
return reportMatcher(new InstanceOf(clazz)).<T>returnNull();
}

public static <T> T verify(T mock) {
return MOCKITO_CORE.verify(mock, times(1));
}

public static VerificationMode times(int wantedNumberOfInvocations) {
return VerificationModeFactory.times(wantedNumberOfInvocations);
}

public static Times times(int wantedNumberOfInvocations) {
return new Times(wantedNumberOfInvocations);
}

public boolean isMock(Object mock) {
return mock != null && isMockitoMock(mock);
}

private <T> boolean isMockitoMock(T mock) {
return Enhancer.isEnhanced(mock.getClass()) && getInterceptor(mock) != null;
}

public CGLIBProxyRealMethod(MockitoMethodProxy methodProxy) {
this.methodProxy = methodProxy;
}

public boolean isVoid() {
return this.method.getReturnType() == Void.TYPE;
}
