public StackTraceElement[] filter(StackTraceElement[] target, boolean keepTop) {
List<StackTraceElement> unfilteredStackTrace = Arrays.asList(target);
int lastBad = -1;
int firstBad = -1;
for (int i = 0; i < unfilteredStackTrace.size(); i++) {
if (!this.isBad(unfilteredStackTrace.get(i))) {
continue;
lastBad = i;
if (firstBad == -1) {
firstBad = i;
if (keepTop && firstBad != -1) {
top = new LinkedList<StackTraceElement>();
List<StackTraceElement> bottom = unfilteredStackTrace.subList(lastBad + 1, unfilteredStackTrace.size());
List<StackTraceElement> filtered = new ArrayList<StackTraceElement>(top);
filtered.addAll(bottom);
return filtered.toArray(new StackTraceElement[]{});
}

public Object handle(Invocation invocation) throws Throwable {
if (mockitoStubber.hasAnswersForStubbing()) {
VerificationMode verificationMode = mockingProgress.pullVerificationMode();
InvocationMatcher invocationMatcher = matchersBinder.bindMatchers(mockingProgress.getArgumentMatcherStorage(),
mockingProgress.validateState();
if (verificationMode != null) {
registeredInvocations.add(invocationMatcher.getInvocation());
mockitoStubber.setInvocationForPotentialStubbing(invocationMatcher);
OngoingStubbingImpl<T> ongoingStubbing = new OngoingStubbingImpl<T>(mockitoStubber, registeredInvocations);
mockingProgress.reportOngoingStubbing(ongoingStubbing);
StubbedInvocationMatcher stubbedInvocation = mockitoStubber.findAnswerFor(invocation);
if (!invocation.isVoid() && stubbedInvocation == null) {
if (stubbedInvocation != null) {
Object ret = mockSettings.getDefaultAnswer().answer(invocation);
mockitoStubber.setInvocationForPotentialStubbing(invocationMatcher);
return ret;
}

public <T> T createMock(Class<T> classToMock, MockingProgress progress, MockSettingsImpl settings) {
creationValidator.validateType(classToMock);
creationValidator.validateExtraInterfaces(classToMock, settings.getExtraInterfaces());
creationValidator.validateMockedType(classToMock, settings.getSpiedInstance());
MockName mockName = new MockName(settings.getMockName(), classToMock);
MockHandler<T> mockHandler = new MockHandler<T>(mockName, progress, new MatchersBinder(), settings);
MethodInterceptorFilter filter = new MethodInterceptorFilter(mockHandler, settings);
Class<?>[] interfaces = settings.getExtraInterfaces();
Class<?>[] ancillaryTypes = interfaces == null ? new Class<?>[0] : interfaces;
Object spiedInstance = settings.getSpiedInstance();
T mock = ClassImposterizer.INSTANCE.imposterise(filter, classToMock, ancillaryTypes);
if (spiedInstance != null) {
return mock;
}

private <T> Class<?> createProxyClass(Class<?> mockedType, Class<?>...interfaces) {
if (mockedType == Object.class) {
Enhancer enhancer = new Enhancer() {
enhancer.setClassLoader(SearchingClassLoader.combineLoadersOf(mockedType));
enhancer.setUseFactory(true);
if (mockedType.isInterface()) {
enhancer.setSuperclass(Object.class);
enhancer.setInterfaces(prepend(mockedType, interfaces));
enhancer.setCallbackTypes(new Class[]{MethodInterceptor.class, NoOp.class});
enhancer.setCallbackFilter(IGNORE_BRIDGE_METHODS);
if (mockedType.getSigners() != null) {
enhancer.setNamingPolicy(MockitoNamingPolicy.INSTANCE);
return enhancer.createClass();
}

