private Class<?> createProxyClass(Class<?> mockedType, Class<?>...interfaces) {
if (mockedType == Object.class) {
Enhancer enhancer = new Enhancer() {
enhancer.setClassLoader(SearchingClassLoader.combineLoadersOf(mockedType));
