private <T> void copyValues(T from, T mock, Class classFrom) {
Field[] fields = classFrom.getDeclaredFields();
for (int i = 0; i < fields.length; i++) {
Field field = fields[i];
if (Modifier.isStatic(field.getModifiers())) {
continue;
