protected List<Matcher> argumentsToMatchers() {
List<Matcher> matchers = new ArrayList<Matcher>(arguments.length);
for (Object arg : arguments) {
if (arg != null && arg.getClass().isArray()) {
matchers.add(new Equals(arg));
return matchers;
}

