public StackTraceElement[] filter(StackTraceElement[] target, boolean keepTop) {
List<StackTraceElement> unfilteredStackTrace = Arrays.asList(target);
int lastBad = -1;
int firstBad = -1;
for (int i = 0; i < unfilteredStackTrace.size(); i++) {
if (!this.isBad(unfilteredStackTrace.get(i))) {
continue;
lastBad = i;
if (firstBad == -1) {
firstBad = i;
if (keepTop && firstBad != -1) {
top = new LinkedList<StackTraceElement>();
List<StackTraceElement> bottom = unfilteredStackTrace.subList(lastBad + 1, unfilteredStackTrace.size());
List<StackTraceElement> filtered = new ArrayList<StackTraceElement>(top);
filtered.addAll(bottom);
return filtered.toArray(new StackTraceElement[]{});
}

public <T> T createMock(Class<T> classToMock, MockingProgress progress, MockSettingsImpl settings) {
creationValidator.validateType(classToMock);
creationValidator.validateExtraInterfaces(classToMock, settings.getExtraInterfaces());
creationValidator.validateMockedType(classToMock, settings.getSpiedInstance());
MockName mockName = new MockName(settings.getMockName(), classToMock);
MockHandler<T> mockHandler = new MockHandler<T>(mockName, progress, new MatchersBinder(), settings);
MethodInterceptorFilter filter = new MethodInterceptorFilter(mockHandler, settings);
Class<?>[] interfaces = settings.getExtraInterfaces();
Class<?>[] ancillaryTypes = interfaces == null ? new Class<?>[0] : interfaces;
Object spiedInstance = settings.getSpiedInstance();
T mock = ClassImposterizer.INSTANCE.imposterise(filter, classToMock, ancillaryTypes);
if (spiedInstance != null) {
return mock;
}

private static void scan(Object testClass, Class<?> clazz) {
AnnotationEngine annotationEngine = new GlobalConfiguration().getAnnotationEngine();
Field[] fields = clazz.getDeclaredFields();
for (Field field : fields) {
for(Annotation annotation : field.getAnnotations()) {
Object mock = annotationEngine.createMockFor(annotation, field);
if (mock != null) {
boolean wasAccessible = field.isAccessible();
field.setAccessible(true);
field.set(testClass, mock);
field.setAccessible(wasAccessible);
}
}

